import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

// Context Provider
import { AuthProvider, useAuth } from './context/AuthContext';

// Components
import NavBar from './components/common/NavBar';
import Footer from './components/common/Footer';

// Pages
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import DashboardPage from './pages/Dashboard';
import CourseListPage from './pages/CourseList';
import CourseDetailPage from './pages/CourseDetail';
import LessonDetailPage from './pages/LessonDetail';
import AssignmentDetailPage from './pages/AssignmentDetail';
import ProfilePage from './pages/ProfilePage';
import NotFound from './pages/NotFound';

// Forms
import CourseForm from './components/courses/CourseForm';
import LessonForm from './components/lessons/LessonForm';
import AssignmentForm from './components/assignments/AssignmentForm';

// Admin components
import AdminPanel from './components/admin/AdminPanel';

// Protected Route Component
const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const { user, loading, isAuthenticated } = useAuth();

  if (loading) {
    return <div className="d-flex justify-content-center p-5">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  // If roles are specified, check if user has the required role
  if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
    return <Navigate to="/dashboard" />;
  }

  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="d-flex flex-column min-vh-100">
          <NavBar />
          <main className="flex-grow-1">
            <Routes>
              {/* Public routes */}
              <Route path="/" element={<HomePage />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="/register" element={<RegisterPage />} />

              {/* Protected routes */}
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <DashboardPage />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/profile" 
                element={
                  <ProtectedRoute>
                    <ProfilePage />
                  </ProtectedRoute>
                } 
              />

              {/* Course routes */}
              <Route 
                path="/courses" 
                element={
                  <ProtectedRoute>
                    <CourseListPage />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/courses/:id" 
                element={
                  <ProtectedRoute>
                    <CourseDetailPage />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/courses/create" 
                element={
                  <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                    <CourseForm />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/courses/edit/:id" 
                element={
                  <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                    <CourseForm />
                  </ProtectedRoute>
                } 
              />

              {/* Lesson routes */}
              <Route 
                path="/lessons/:id" 
                element={
                  <ProtectedRoute>
                    <LessonDetailPage />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/courses/:courseId/lesson/create" 
                element={
                  <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                    <LessonForm />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/lessons/edit/:lessonId" 
                element={
                  <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                    <LessonForm />
                  </ProtectedRoute>
                } 
              />

              {/* Assignment routes */}
              <Route 
                path="/assignments/:id" 
                element={
                  <ProtectedRoute>
                    <AssignmentDetailPage />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/assignments/:id/submit" 
                element={
                  <ProtectedRoute allowedRoles={['student']}>
                    <AssignmentForm />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/courses/:courseId/assignment/create" 
                element={
                  <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                    <AssignmentForm />
                  </ProtectedRoute>
                } 
              />

              <Route 
                path="/assignments/edit/:assignmentId" 
                element={
                  <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                    <AssignmentForm />
                  </ProtectedRoute>
                } 
              />

              {/* Admin routes */}
              <Route 
                path="/admin" 
                element={
                  <ProtectedRoute allowedRoles={['admin']}>
                    <AdminPanel />
                  </ProtectedRoute>
                } 
              />

              {/* 404 Not found */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
