import api from './api';

export const getAssignments = async (params = {}) => {
  try {
    const response = await api.get('/assignments', { params });
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const getAssignmentById = async (id) => {
  try {
    const response = await api.get(`/assignments/${id}`);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const createAssignment = async (assignmentData) => {
  try {
    const response = await api.post('/assignments', assignmentData);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const updateAssignment = async (assignmentId, assignmentData) => {
  try {
    const response = await api.patch(`/assignments/${assignmentId}`, assignmentData);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const deleteAssignment = async (assignmentId) => {
  try {
    const response = await api.delete(`/assignments/${assignmentId}`);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const submitAssignment = async (assignmentId, submissionData) => {
  try {
    const response = await api.post(`/assignments/${assignmentId}/submit`, submissionData);
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const gradeAssignment = async (assignmentId, submissionId, gradingData) => {
  try {
    const response = await api.post(`/assignments/${assignmentId}/grade/${submissionId}`, gradingData);
    return response.data;
  } catch (error) {
    throw error;
  }
};
